<?php	/**	* Database	*	* @author   Hamid Khairy <hamid.khairy@seznam.cz>	* @version  $Revision: 1.0.0.0 $	* @access   public	*/	class Database	{		private	static	$HOSTNAME	= "127.0.0.1";		private	static	$USERNAME	= "root";		private	static	$PASSWORD	= "";		private	static	$DATABASE	= "fantasy_wars";				private static	$connection	= null;				public static function Get()		{			if ( is_null( self::$connection ) )				self::$connection = new mysqli( self::$HOSTNAME, self::$USERNAME, self::$PASSWORD, self::$DATABASE );						return self::$connection;		}	}?>